// 第三方
import gulp from 'gulp';
import zip from 'gulp-zip';

// 自定义
import util from '../util.js';
import config from '../config.js';

/************************************ zip包 ************************************/
gulp.task('task_zip', () => {
  console.log(`>>>>>>>>>>>>>>> zip任务开始编译。${util.getNow()}`);

  return gulp.src(`${config.path.dist}/**/*`)

    .pipe(zip(`${util.getProjectName()}.zip`))

    .pipe(gulp.dest(''));
});
